{
  "name": "complex-test-app",
  "version": "2.1.0",
  "description": "A complex application for comprehensive restoration testing",
  "main": "src/index.js",
  "scripts": {
    "start": "node src/index.js",
    "dev": "nodemon src/index.js",
    "build": "webpack --mode production",
    "test": "jest"
  },
  "author": "Jane Smith <jane.smith@company.com>",
  "license": "MIT",
  "dependencies": {
    "express": "^4.18.0",
    "dotenv": "^16.0.0",
    "cors": "^2.8.5"
  },
  "devDependencies": {
    "nodemon": "^2.0.0",
    "webpack": "^5.0.0",
    "jest": "^29.0.0"
  }
}

require('dotenv').config();
const express = require('express');
const cors = require('cors');

const app = express();
const port = process.env.PORT || 3000;

app.use(cors());
app.use(express.json());

app.get('/', (req, res) => {
  res.json({
    name: 'complex-test-app',
    version: '2.1.0',
    author: 'Jane Smith',
    environment: process.env.NODE_ENV || 'development'
  });
});

app.get('/api/health', (req, res) => {
  res.json({ status: 'healthy', timestamp: new Date().toISOString() });
});

app.listen(port, () => {
  console.log(`complex-test-app server running on port ${port}`);
});

module.exports = {
  development: {
    url: process.env.DATABASE_URL || 'postgresql://localhost:5432/complex_test_app',
    dialect: 'postgres'
  },
  production: {
    url: process.env.DATABASE_URL,
    dialect: 'postgres',
    ssl: true
  }
};
